// Dohvat podataka i prikaz FotoPlus dijela
function loadContent(mid) {
    jQuery.get("/tabid/207/mid/" + mid + "/dnnprintmode/true/Default.aspx?SkinSrc=[G]Skins%2f_default%2fNo+Skin&ContainerSrc=[G]Containers%2f_default%2fNo+Container", ubaciHtml);
}
function ubaciHtml(data) {
    var data2 = jQuery(data).find('.RandomView SPAN A');
    jQuery('#content').empty().append(data2);
    jQuery('#content A').wrap('<li class="gallery_item"></li>');
    jQuery('#content').wrapInner('<ul id="fotoNewsGal" class="jcarousel-skin-sdgallery1"></ul>');
    var galleryName = jQuery('#content A:first SPAN').text();
    jQuery('#content #fotoNewsGal').after('<p class="galleryName">' + galleryName + '</p>');
    jQuery('#content a.lightbox').lightBox();
    jQuery('#fotoNewsGal').jcarousel();
}
function fpChangeTab(e){
	var targ;
	if(!e) var e = window.event;
	if(e.target) targ = e.target;
	else if(e.srcElement) targ = e.srcElement;
	if(targ.nodeType == 3) targ = targ.parentNode;
	if(targ.nodeName.toLowerCase() === "a"){
		var aid = targ.getAttribute('id');
		jQuery("#fotoCatTabs a").removeClass("active");
		targ.setAttribute("class","active");
		switch (aid) {
			case "l1":
				loadContent('864');
				return false;
				break;
			case "l2":
				loadContent('865');
				return false;
				break;
			case "l3":
				loadContent('866');
				return false;
				break;
			case "l4":
				loadContent('867');
				return false;
				break;
		}
	}
}

// Dohvat title i podešavanje active na thumbovima u TopVideo
function tvGetVideoTitle(e){
	var targ;
	if(!e) var e = window.event;
	if(e.target) targ = e.target;
	else if(e.srcElement) targ = e.srcElement;
	if(targ.nodeType == 3) targ = targ.parentNode;
	if(targ.nodeName.toLowerCase() === "a"){
		var atitle = targ.getAttribute('title');
		jQuery("#videoGal a").removeClass("active");
		jQuery("#videoGal a").empty();
		targ.setAttribute("class","active");
		jQuery("#videoGal a.active").html("<span></span>");
		jQuery(".frontVideoGallery #videoTitle").empty().text(atitle);
	}
}
function tvSetFirstVideoTitle(){
	var atitle = jQuery("#videoGal li:first a")[0].getAttribute("title");
	jQuery("#videoGal li:first a").attr("class","active").html("<span></span>");
	jQuery(".frontVideoGallery #videoTitle").empty().text(atitle);
}

// Ureðivanje foto galerije na dnu
function setSimpleGalleryCarusel(galleryId, galleryClass) {
    var pictureLinks = jQuery(".RandomView span").find('A');
    jQuery('.fotoGallery .RandomView').empty().append(pictureLinks);
    jQuery('.fotoGallery .RandomView A').wrap('<li class="gallery_item"></li>');
    jQuery('.fotoGallery .RandomView').wrapInner('<ul id="' + galleryId + '" class="jcarousel-skin-' + galleryClass + '"></ul>');
    var galleryTitle = jQuery('.fotoGallery .RandomView A:first').text();
	var galleryDescription = jQuery('.fotoGallery .RandomView A').get(0).getAttribute('title');
    jQuery('.fotoGallery .RandomView #' + galleryId).after('<h3>' + galleryTitle + '</h3>');
	jQuery('.fotoGallery .RandomView h3').after('<p>' + galleryDescription + '</p>');
    /*jQuery('#content a.lightbox').lightBox();*/
    jQuery('#' + galleryId).jcarousel();
}

// Pokaži baner div
function showBanerDivs() {
	jQuery('.banner, .newspaperHolder, .banShow').each(function()	{ 
		if (jQuery(this).children().size() > 1) {
		jQuery(this).show();
		}
	}); 
}


//zatvori floater baner
function closeBaner(divId) {
  	jQuery("#" + divId).remove();
}


/* Otvaranje formi za piši uredniku i piši prijatelju */
function openEditorForm() {
	jQuery(".formPanelEditor").slideToggle("slow");
	jQuery(".formPanelFriend").slideUp("slow");
	return false;
}
function openFriendForm() {
	jQuery(".formPanelFriend").slideToggle("slow");
	jQuery(".formPanelEditor").slideUp("slow");
	return false;
}

/* Skrivanje komentara */
function showComment() {
	jQuery("a#moreLink").click(function() {
		var items = jQuery(".commentsWrap > .comment:hidden");
        if(items.length > 0)
			items.slice(0, 5).show();	
		else
			jQuery(".commentNavig a").toggle();
		return false;
	});
	
	jQuery("a#closeLink").click(function() {
		jQuery(".commentsWrap .comment").filter(':gt(4)').hide();
	 	jQuery(this).hide();
		jQuery(".commentNavig a#moreLink").show();
		return false;
	});
	
	jQuery("a#moreLink").click();
	if (jQuery(".commentsWrap .comment").length <= 5)
		jQuery(".commentNavig").hide();
	else
		jQuery(".commentNavig").show();
}

/* Stopiranje jCarusela na mouse over */
function mycarousel_initCallback(carousel) {
    // Disable autoscrolling if the user clicks the prev or next button.
    carousel.buttonNext.bind('click', function() {
        carousel.startAuto(0);
    });
    carousel.buttonPrev.bind('click', function() {
        carousel.startAuto(0);
    });
    // Pause autoscrolling if the user moves with the cursor over the clip.
    carousel.clip.hover(function() {
        carousel.stopAuto();
    }, function() {
        carousel.startAuto();
    });
}

/* Podešavanje jednakih visina elementima */
var maxHeight = 0;
function setHeight(column) {
    column = jQuery(column);
    column.each(function() {       
        if(jQuery(this).height() > maxHeight) {
            maxHeight = jQuery(this).height();;
        }
    });
    column.height(maxHeight);
}

